import React from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, Alert, Dimensions } from 'react-native';
import { NativeStackScreenProps } from '@react-navigation/native-stack';
import { LinearGradient } from 'expo-linear-gradient';

type Props = NativeStackScreenProps<any, any>;
const { width } = Dimensions.get('window');

export default function WelcomePage({ navigation }: Props) {
  const handleQuit = () => {
    Alert.alert(
      'Quitter l\'application',
      'Voulez-vous vraiment quitter ?',
      [
        { text: 'Annuler', style: 'cancel' },
        { text: 'Quitter', onPress: () => {}, style: 'destructive' }
      ]
    );
  };

  return (
    <View style={styles.container}>
      {/* AppBar avec gradient */}
      <View style={styles.appbar}>
        <Text style={styles.title}>Atlas Géographique</Text>
        <TouchableOpacity style={styles.quitButton} onPress={handleQuit}>
          <Text style={styles.quitButtonText}>✕</Text>
        </TouchableOpacity>
      </View>

      {/* Contenu central */}
      <View style={styles.content}>
        <View style={styles.imageContainer}>
          <Image source={require('../../assets/globe.png')} style={styles.image} resizeMode="contain" />
        </View>

        <Text style={styles.welcomeTitle}>Bienvenue</Text>
        <Text style={styles.welcomeText}>Découvrez les pays du monde</Text>
        <Text style={styles.welcomeSubtext}>Explorez les capitales, populations et cultures</Text>

        {/* Bouton Explorer avec effet */}
        <TouchableOpacity 
          style={styles.button} 
          onPress={() => navigation.replace('MainDrawer')}
          activeOpacity={0.8}
        >
          <Text style={styles.buttonText}>Explorer →</Text>
        </TouchableOpacity>
      </View>

      {/* Footer */}
      <View style={styles.footer}>
        <Text style={styles.footerText}>Examen - Développement Mobile</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { 
    flex: 1, 
    backgroundColor: '#0a1929',
  },
  appbar: { 
    width: '100%', 
    paddingTop: 50, 
    paddingBottom: 20, 
    paddingHorizontal: 20,
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: 'rgba(255,255,255,0.05)',
    borderBottomWidth: 1,
    borderBottomColor: 'rgba(255,255,255,0.1)'
  },
  title: { 
    color: '#fff', 
    fontSize: 22, 
    fontWeight: '700',
    letterSpacing: 0.5
  },
  quitButton: {
    width: 36,
    height: 36,
    borderRadius: 18,
    backgroundColor: 'rgba(255,59,48,0.15)',
    alignItems: 'center',
    justifyContent: 'center',
    borderWidth: 1,
    borderColor: 'rgba(255,59,48,0.3)'
  },
  quitButtonText: {
    color: '#ff3b30',
    fontSize: 20,
    fontWeight: '600'
  },
  content: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingHorizontal: 20
  },
  imageContainer: {
    width: 280,
    height: 280,
    borderRadius: 140,
    backgroundColor: 'rgba(255,255,255,0.05)',
    alignItems: 'center',
    justifyContent: 'center',
    marginBottom: 40,
    shadowColor: '#1e90ff',
    shadowOffset: { width: 0, height: 10 },
    shadowOpacity: 0.3,
    shadowRadius: 30,
    elevation: 15
  },
  image: { 
    width: 240, 
    height: 240
  },
  welcomeTitle: {
    color: '#fff',
    fontSize: 32,
    fontWeight: '700',
    marginBottom: 10,
    letterSpacing: 1
  },
  welcomeText: { 
    color: '#e3f2fd', 
    fontSize: 20, 
    textAlign: 'center',
    fontWeight: '500',
    marginBottom: 8
  },
  welcomeSubtext: {
    color: 'rgba(255,255,255,0.6)',
    fontSize: 14,
    textAlign: 'center',
    marginBottom: 40
  },
  button: { 
    backgroundColor: '#1e88e5',
    paddingVertical: 16, 
    paddingHorizontal: 60, 
    borderRadius: 30,
    shadowColor: '#1e88e5',
    shadowOffset: { width: 0, height: 8 },
    shadowOpacity: 0.4,
    shadowRadius: 12,
    elevation: 8,
    borderWidth: 1,
    borderColor: 'rgba(255,255,255,0.2)'
  },
  buttonText: { 
    color: '#fff', 
    fontWeight: '700',
    fontSize: 18,
    letterSpacing: 1
  },
  footer: {
    paddingVertical: 20,
    alignItems: 'center'
  },
  footerText: {
    color: 'rgba(255,255,255,0.4)',
    fontSize: 12
  }
});
