import React from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { NativeStackScreenProps } from '@react-navigation/native-stack';

type Props = NativeStackScreenProps<any, any>;

export default function WelcomePage({ navigation }: Props) {
  return (
    <View style={styles.container}>
      <View style={styles.appbar}>
        <Text style={styles.title}>Atlas Géographique</Text>
      </View>

      <Image source={require('../../assets/globe.png')} style={styles.image} resizeMode="contain" />

      <Text style={styles.welcomeText}>Découvrez les pays du monde</Text>

      <TouchableOpacity style={styles.button} onPress={() => navigation.replace('MainDrawer')}>
        <Text style={styles.buttonText}>Explorer</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: { flex: 1, backgroundColor: '#0f1724', alignItems: 'center', justifyContent: 'space-between', paddingVertical: 40 },
  appbar: { width: '100%', paddingTop: 40, paddingBottom: 10, alignItems: 'center' },
  title: { color: '#fff', fontSize: 20, fontWeight: '700' },
  image: { width: 220, height: 220, marginTop: 20 },
  welcomeText: { color: '#fff', fontSize: 18, textAlign: 'center', marginTop: 10 },
  button: { backgroundColor: '#1776ff', paddingVertical: 12, paddingHorizontal: 40, borderRadius: 14, marginBottom: 20 },
  buttonText: { color: '#fff', fontWeight: '600' }
});
